site.headUser:
#videobox {
  width: 100%;
  max-width: 270px;
  height: 300px;
  margin: 2.5px;
  background: #000;
}

#videobox p {
    position: fixed;
    top: 30%;
    margin: 1em;
    color: white;
}

.youtube-player {
  position: relative;
  padding-bottom: 56.25%;
  height: 0;
  overflow: hidden;
  max-width: 100%;
  background: #000;
  margin: 5px;
}

.youtube-player iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 100;
  background: transparent;
}

.youtube-player img {
  object-fit: cover;
  display: block;
  left: 0;
  bottom: 0;
  margin: auto;
  max-width: 100%;
  width: 100%;
  position: absolute;
  right: 0;
  top: 0;
  border: none;
  height: auto;
  -webkit-transition: 0.4s all;
  -moz-transition: 0.4s all;
  transition: 0.4s all;
}

.youtube-player .play {
    width: 72px;
    height: 72px;
    left: 50%;
    top: 50%;
    margin-left: -36px;
    margin-top: -36px;
    position: absolute;
    background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 68 48"><path d="M66.52 7.74c-.78-2.93-2.49-5.41-5.42-6.19C55.79.13 34 0 34 0S12.21.13 6.9 1.55c-2.93.78-4.63 3.26-5.42 6.19C.06 13.05 0 24 0 24s.06 10.95 1.48 16.26c.78 2.93 2.49 5.41 5.42 6.19C12.21 47.87 34 48 34 48s21.79-.13 27.1-1.55c2.93-.78 4.64-3.26 5.42-6.19C67.94 34.95 68 24 68 24s-.06-10.95-1.48-16.26z" fill="red"/><path d="M45 24 27 14v20" fill="white"/></svg>') no-repeat;
    filter: grayscale(100%);
    border: none;
}

.youtube-player:hover .play,
.youtube-player .play:focus {
  filter: none;
  -webkit-transition: 0.4s all;
  -moz-transition: 0.4s all;
  transition: 0.4s all;
}

img.yt-img-shadow {
  -webkit-mask-image: -webkit-radial- gradient(circle, white 100%, black 100%);
  -webkit-border-radius: 30px; 
  -moz-border-radius: 30px;
  border-radius:30px; 
  overflow: hidden; 
  z-index: 2;
}

img#img.style-scope.yt-img-shadow {
  width: 40px;
  position: relative;
	top: 10px;  
	left: -40%; 
}


